#!/bin/bash
# (Optionally) replair, analyze and optimize tables.
# Flush mysql to free up disk space.
# Managed by Puppet: do not edit this file directly. It will be overwritten!
set -e

# exit quietly if there is no client configuration or no daemon running
if ! [[ -f /root/.my.cnf ]] || ! pgrep mysql >/dev/null; then
    exit 0
fi

mysql <<__EOT__
reset master;
flush logs;
__EOT__
